mapper.select:
select count(*) from board where boardtype = #{type}

	and boardnum in (select boardnum from duck where userid=#{userid})


	and ${column} like '%${find}%'

select boardnum,userid,boardtype,subject,content,regdate, readcnt, recmd, file1, pic, usetech, price, schedule from board where boardtype = #{type}

	and boardnum = #{boardnum}


	and ${column} like '%${find}%'


	and usetech like '%${tech}%'

order by regdate desc

	limit #{startrow},${limit}

select * from board where boardnum in (select boardnum from duck where userid=#{userid}) and boardtype = #{type}

	and boardnum = #{boardnum}


	and ${column} like '%${find}%'

order by regdate desc

	limit #{startrow},${limit}

select * from board where boardtype = 1

	and boardnum = #{boardnum}

order by regdate desc

select * from board where boardtype = 3

	and boardnum = #{boardnum}

order by regdate desc
