mapper.select:
select count(*) from board where boardtype = #{type}

	and userid = #{id}


	and boardnum in (select boardnum from duck where userid=#{userid} and ducktype = #{ducktype})


	and ${column} like '%${find}%'

select boardnum,userid,boardtype,subject,content,regdate, readcnt, recmd, file1 fileurl, pic, usetech, price, schedule, maxperson, nowperson from board where boardtype = #{type}

	and boardnum = #{boardnum}


	and ${column} like '%${find}%'


	and usetech like '%${tech}%'

order by regdate desc

	limit #{startrow},${limit}

select * from board where boardnum in (select boardnum from duck where userid=#{userid} and ducktype = #{ducktype}) and boardtype = #{type}

	and boardnum = #{boardnum}


	and ${column} like '%${find}%'


order by regdate desc

	limit #{startrow},${limit}

select * from board where boardnum in (select boardnum from duck where userid=#{userid} and ducktype = #{ducktype})

	and boardnum = #{boardnum}


	and ${column} like '%${find}%'

order by regdate desc

	limit #{startrow},${limit}

select * from board where userid=#{userid} and boardtype = #{type}

